#include <stdio.h>
#include <stdlib.h>
#include <time.h>


char str[] =
"";

int *strToV(char *s) {
    int i;
    int *v = calloc(1000, sizeof(int));
    for (i = 0; i < 1000; ++i) {
        char c = s[i];
        v[i] = atoi(&c);
        // printf("%c\n", *(s+i));
        // printf("%d\n", v[i]);
    }
    return v;
}

void printv(int *v) {
    int i;
    for (i = 0; i < 1000; ++i) {
        printf("positon %d: %d\n", i, v[i]);
    }
}

int main() {
    clock_t start = clock();
    int *v = strToV(str);
    int i, j;
    long long max = 0, pro = 1;
    FILE *fp = fopen("c1.out", "w");
    for (i = 0; i < 1000-13; ++i) {
        pro = 1;
        for (j = i; j < i+13; ++j) {
            // if (v[j] == 0) break;
            // printf("%d ", v[j]);
            fprintf(fp, "%d ", v[j]);
            pro *= (long long)v[j];
        }
        if (pro > max) {
                max = pro;
        }
        if (pro < 0) {
            printf("Error: %d\n", pro);
            exit(1);
        }
        // printf("\nPro: %d\n", pro);
        fprintf(fp, "\nPro: %d\n", pro);


    }
    clock_t end = clock();
    printf("Result: %d\n", max);
    fprintf(fp, "Result: %d\n", max);
    printf("Use time: %d\n", end);
    // printf("Count: %d\n", count);
    fclose(fp);
    free(v);
}